						'name' => 'Welcom to Auteur 02',
						'data' => '[{"id":"40d0ad9","elType":"section","settings":{"padding":{"unit":"px","top":"50","right":"0","bottom":"0","left":"0","isLinked":false}},"elements":[{"id":"85986c5","elType":"column","settings":{"_column_size":100,"_inline_size":null},"elements":[{"id":"27cbd3d","elType":"widget","settings":{"image":{"url":"https:\\/\\/auteur.g5plus.net\\/elementor\\/wp-content\\/uploads\\/2018\\/11\\/single-image-01.png","id":248,"alt":"","source":"library"},"image_size":"full"},"elements":[],"widgetType":"image"},{"id":"31a6dc4","elType":"widget","settings":{"heading_title":"Welcom to Auteur","heading_description":"<p>A MONTHLY BOOKS REVIEW PUBLICATION<\\/p>","heading_align":"center","heading_description_typography_typography":"custom","heading_description_typography_font_family":"Libre Baskerville","heading_description_typography_letter_spacing":{"unit":"px","size":5,"sizes":[]},"heading_description_spacing_hover":{"unit":"px","size":24,"sizes":[]},"_margin":{"unit":"px","top":"15","right":"0","bottom":"0","left":"0","isLinked":false},"__globals__":{"heading_description_text_color_color":"globals\\/colors?id=accent"},"heading_title_typography_typography":"custom","heading_title_typography_font_size_mobile":{"unit":"px","size":34,"sizes":[]},"heading_description_typography_font_size_mobile":{"unit":"px","size":16,"sizes":[]}},"elements":[],"widgetType":"ube-heading"},{"id":"a14d404","elType":"widget","settings":{"editor":"<p class=\\"mg-bottom-55 sm-mg-bottom-25\\">Imagine two men facing each other, pointing past one another. One is pointing at a tornado that is coming, and the other at a raging fire headed towards them. Each sees their own truth and is angry at the sight of the other\\u2019s hand. Each feels that the other\\u2019s hand is \\u201cwrong.\\u201d This may seem silly, but replace the tornado and fire with any modern issues, and the hands with words, and this scene describes how we often try to communicate.<\\/p>\\n<p class=\\"primary-font fw-bold head